SELECT BOARD_TYPE ,BOARD_NUM ,BOARD_TITLE ,BOARD_COMMENT ,TOTAL_CNT FROM ( SELECT BOARD_TYPE ,BOARD_NUM ,BOARD_TITLE ,BOARD_COMMENT ,ROW_NUMBER() OVER(ORDER BY BOARD_NUM DESC) AS NUMROW ,COUNT(*) OVER() AS TOTAL_CNT FROM BOARD where 1=1
and user_id = #{selectCheckBox.checkBox2}
and user_id = #{selectCheckBox.checkBox3}
) WHERE NUMROW BETWEEN (#{pageNo}-1)*10 + 1 AND (#{pageNo})*10

	UPDATE BOARD SET
   		BOARD_TITLE = #{boardTitle},
   		BOARD_COMMENT = #{boardComment}

	WHERE 	BOARD_TYPE = #{boardType}
	AND 	BOARD_NUM  = #{boardNum}

DELETE BOARD 

WHERE 	BOARD_TYPE = #{boardType}
AND 	BOARD_NUM  = #{boardNum}
